import './ManageUsers.css';

const ManageClients = () => {
  const { user, isAdmin } = useAuth();
  const [clients, setClients] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [filterStatus, setFilterStatus] = useState('all');

  useEffect(() => {
    if (!isAdmin) {
      setError('Access denied. Admin permissions required.');
      setLoading(false);
      return;
    }
    fetchClients();
  }, [isAdmin]);

  const fetchClients = async () => {
    try {
      setLoading(true);
      const response = await adminAPI.getClients();
      setClients(response.data.clients || []);
      setError('');
    } catch (error) {
      console.error('Error fetching clients:', error);
      setError('Failed to load clients');
      // Mock data for demo purposes
      setClients([
        {
          _id: '1',
          username: 'client1',
          email: 'client1@example.com',
          fullName: 'Alice Johnson',
          phone: '+1-555-1234',
          isActive: true,
          registeredAt: '2024-01-10T08:30:00Z',
          lastLogin: '2024-01-25T14:20:00Z',
          ticketsPurchased: 5,
          totalSpent: 250.00
        },
        {
          _id: '2',
          username: 'client2',
          email: 'client2@example.com',
          fullName: 'Bob Smith',
          phone: '+1-555-5678',
          isActive: true,
          registeredAt: '2024-01-12T16:45:00Z',
          lastLogin: '2024-01-24T10:15:00Z',
          ticketsPurchased: 2,
          totalSpent: 120.00
        },
        {
          _id: '3',
          username: 'client3',
          email: 'client3@example.com',
          fullName: 'Carol Davis',
          phone: '+1-555-9012',
          isActive: false,
          registeredAt: '2024-01-08T12:00:00Z',
          lastLogin: '2024-01-15T09:30:00Z',
          ticketsPurchased: 1,
          totalSpent: 75.00
        }
      ]);
    } finally {
      setLoading(false);
    }
  };

  const handleToggleStatus = async (clientId, currentStatus) => {
    try {
      await adminAPI.toggleClientStatus(clientId, !currentStatus);
      setClients(clients.map(client => 
        client._id === clientId 
          ? { ...client, isActive: !currentStatus }
          : client
      ));
    } catch (error) {
      console.error('Error toggling client status:', error);
      alert('Failed to update client status');
    }
  };

  const handleSendEmail = (client) => {
    // In a real app, this would open an email composer or send via API
    window.open(`mailto:${client.email}`, '_blank');
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  const filteredClients = clients.filter(client => {
    const matchesSearch = !searchTerm || 
      client.fullName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      client.username.toLowerCase().includes(searchTerm.toLowerCase()) ||
      client.email.toLowerCase().includes(searchTerm.toLowerCase());

    const matchesStatus = filterStatus === 'all' || 
      (filterStatus === 'active' && client.isActive) ||
      (filterStatus === 'inactive' && !client.isActive);

    return matchesSearch && matchesStatus;
  });

  if (loading) {
    return <Loading message="Loading clients..." />;
  }

  if (!isAdmin) {
    return (
      <div className="access-denied">
        <i className="fas fa-lock"></i>
        <h2>Access Denied</h2>
        <p>You need admin permissions to access this page.</p>
      </div>
    );
  }

  return (
    <div className="manage-users-page">
      <div className="container">
        <div className="page-header">
          <h1>Manage Clients</h1>
          <p>View and manage client accounts and activity</p>
        </div>

        <div className="filters-bar">
          <div className="search-box">
            <i className="fas fa-search"></i>
            <input
              type="text"
              placeholder="Search clients..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>

          <select
            value={filterStatus}
            onChange={(e) => setFilterStatus(e.target.value)}
            className="filter-select"
          >
            <option value="all">All Status</option>
            <option value="active">Active</option>
            <option value="inactive">Inactive</option>
          </select>

          <div className="users-count">
            {filteredClients.length} of {clients.length} Clients
          </div>
        </div>

        {error && (
          <div className="alert alert-error">
            <i className="fas fa-exclamation-triangle"></i>
            {error}
            <button onClick={fetchClients} className="btn btn-sm">
              Try Again
            </button>
          </div>
        )}

        {/* Summary Cards */}
        <div className="summary-grid">
          <div className="summary-card">
            <div className="summary-icon">
              <i className="fas fa-users"></i>
            </div>
            <div className="summary-content">
              <h3>{clients.length}</h3>
              <p>Total Clients</p>
            </div>
          </div>

          <div className="summary-card">
            <div className="summary-icon">
              <i className="fas fa-user-check"></i>
            </div>
            <div className="summary-content">
              <h3>{clients.filter(c => c.isActive).length}</h3>
              <p>Active Clients</p>
            </div>
          </div>

          <div className="summary-card">
            <div className="summary-icon">
              <i className="fas fa-ticket-alt"></i>
            </div>
            <div className="summary-content">
              <h3>{clients.reduce((sum, c) => sum + (c.ticketsPurchased || 0), 0)}</h3>
              <p>Total Tickets Sold</p>
            </div>
          </div>

          <div className="summary-card">
            <div className="summary-icon">
              <i className="fas fa-dollar-sign"></i>
            </div>
            <div className="summary-content">
              <h3>{formatCurrency(clients.reduce((sum, c) => sum + (c.totalSpent || 0), 0))}</h3>
              <p>Total Revenue</p>
            </div>
          </div>
        </div>

        {filteredClients.length === 0 ? (
          <div className="empty-state">
            <i className="fas fa-users"></i>
            <h3>No Clients Found</h3>
            <p>{searchTerm ? 'No clients match your search criteria.' : 'No clients have registered yet.'}</p>
          </div>
        ) : (
          <div className="users-table">
            <table>
              <thead>
                <tr>
                  <th>Client</th>
                  <th>Contact</th>
                  <th>Activity</th>
                  <th>Last Login</th>
                  <th>Status</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {filteredClients.map(client => (
                  <tr key={client._id}>
                    <td>
                      <div className="user-info">
                        <div className="user-avatar">
                          <i className="fas fa-user"></i>
                        </div>
                        <div>
                          <div className="user-name">{client.fullName || client.username}</div>
                          <div className="user-username">@{client.username}</div>
                          <div className="user-meta">
                            Joined {formatDate(client.registeredAt)}
                          </div>
                        </div>
                      </div>
                    </td>
                    <td>
                      <div className="contact-info">
                        <div>{client.email}</div>
                        {client.phone && <div className="phone">{client.phone}</div>}
                      </div>
                    </td>
                    <td>
                      <div className="activity-info">
                        <div className="activity-stat">
                          <i className="fas fa-ticket-alt"></i>
                          {client.ticketsPurchased || 0} tickets
                        </div>
                        <div className="activity-stat">
                          <i className="fas fa-dollar-sign"></i>
                          {formatCurrency(client.totalSpent || 0)}
                        </div>
                      </div>
                    </td>
                    <td>
                      {client.lastLogin ? formatDate(client.lastLogin) : 'Never'}
                    </td>
                    <td>
                      <span className={`status-badge ${client.isActive ? 'active' : 'inactive'}`}>
                        {client.isActive ? 'Active' : 'Inactive'}
                      </span>
                    </td>
                    <td>
                      <div className="action-buttons">
                        <button
                          onClick={() => handleSendEmail(client)}
                          className="btn btn-sm btn-outline"
                          title="Send Email"
                        >
                          <i className="fas fa-envelope"></i>
                        </button>
                        <button
                          onClick={() => handleToggleStatus(client._id, client.isActive)}
                          className={`btn btn-sm ${client.isActive ? 'btn-outline' : 'btn-success'}`}
                          title={client.isActive ? 'Deactivate' : 'Activate'}
                        >
                          <i className={`fas ${client.isActive ? 'fa-ban' : 'fa-check'}`}></i>
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default ManageClients;
