import React from "react";
import { Link } from "react-router-dom";
import { useAuth } from "../App";
import "./EventCard.css";

const EventCard = ({ event, onEdit, onDelete, showActions = false }) => {
  const { isAuthenticated } = useAuth();

  const formatDate = (dateString) => {
    const options = {
      year: "numeric",
      month: "long",
      day: "numeric",
      hour: "2-digit",
      minute: "2-digit",
    };
    return new Date(dateString).toLocaleDateString("en-US", options);
  };

  const formatPrice = (price) => {
    return new Intl.NumberFormat("en-US", {
      style: "currency",
      currency: "USD",
    }).format(price);
  };

  return (
    <div className="event-card">
      <div className="event-card-header">
        <h3 className="event-title">{event.name}</h3>
        {showActions && (
          <div className="event-actions">
            <button
              className="btn btn-sm btn-primary"
              onClick={() => onEdit(event)}
              title="Edit Event"
            >
              <i className="fas fa-edit"></i>
            </button>
            <button
              className="btn btn-sm btn-danger"
              onClick={() => onDelete(event)}
              title="Delete Event"
            >
              <i className="fas fa-trash"></i>
            </button>
          </div>
        )}
      </div>

      <div className="event-details">
        <div className="event-info">
          <div className="info-item">
            <i className="fas fa-calendar"></i>
            <span>{formatDate(event.date)}</span>
          </div>

          <div className="info-item">
            <i className="fas fa-map-marker-alt"></i>
            <span>{event.location}</span>
          </div>

          <div className="info-item">
            <i className="fas fa-dollar-sign"></i>
            <span>{formatPrice(event.charges)}</span>
          </div>
        </div>

        {event.description && (
          <p className="event-description">
            {event.description.length > 100
              ? `${event.description.substring(0, 100)}...`
              : event.description}
          </p>
        )}
      </div>

      <div className="event-card-footer">
        <Link to={`/events/${event._id}`} className="btn btn-outline">
          View Details
        </Link>

        {isAuthenticated && !showActions && (
          <Link to={`/purchase/${event._id}`} className="btn btn-primary">
            <i className="fas fa-ticket-alt"></i>
            Buy Ticket
          </Link>
        )}
      </div>

      {event.createdBy && (
        <div className="event-meta">
          <small>Created by: {event.createdBy.username || "Admin"}</small>
        </div>
      )}
    </div>
  );
};

export default EventCard;
